USE ECommerceProject;

CREATE TABLE [User] (
   [id] INT IDENTITY(1,1) PRIMARY KEY,
   [name] NVARCHAR(50) NOT NULL,
   [avatar] NVARCHAR(MAX),
   [username] NVARCHAR(50) UNIQUE NOT NULL,
   [password] NVARCHAR(255) NOT NULL,
   [sex] NVARCHAR(10) NOT NULL,
   [birthday] DATE NOT NULL,
   [email] NVARCHAR(100) NOT NULL,
   [address] NVARCHAR(200) NOT NULL,
   [isSeller] BIT NOT NULL,
   [isAdmin] BIT NOT NULL
);

CREATE TABLE [Brand] (
   [brand_id] INT IDENTITY(1,1) PRIMARY KEY,
   [brand_name] NVARCHAR(50) NOT NULL
);

CREATE TABLE [Category] (
   [category_id] INT IDENTITY(1,1) PRIMARY KEY,
   [category_name] NVARCHAR(50) NOT NULL
);

CREATE TABLE [Product] (
   [product_id] INT IDENTITY(1,1) PRIMARY KEY,
   [seller_id] INT NOT NULL,
   [product_name] NVARCHAR(100) NOT NULL,
   [product_description] NVARCHAR(MAX),
   [category_id] INT NOT NULL,
   [brand_id] INT NOT NULL,
   [price] MONEY NOT NULL,
   [quantity] INT NOT NULL,
   [image] NVARCHAR(MAX),
   [discount] INT,

   CONSTRAINT [fk_Product_Category] FOREIGN KEY ([category_id])
   REFERENCES [Category]([category_id]),

   CONSTRAINT [fk_Product_Seller] FOREIGN KEY ([seller_id])
   REFERENCES [User]([id]),

   CONSTRAINT [fk_Product_Brand] FOREIGN KEY ([brand_id])
   REFERENCES [Brand]([brand_id])
);

CREATE TABLE [Order] (
  [OrderID] INT IDENTITY(1,1) PRIMARY KEY,
  [user_id] INT NOT NULL,
  [OrderDate] DATETIME NOT NULL,
  [received_date] DATETIME,
  [address] NVARCHAR(200) NOT NULL,
  [payment_method] NVARCHAR(200) NOT NULL,
  [Status] VARCHAR(20) NOT NULL,
  [total_price] DECIMAL(10,2) NOT NULL,

  CONSTRAINT [fk_Order_User] FOREIGN KEY ([user_id])
  REFERENCES [User]([id])
);

CREATE TABLE [OrderItem] (
  [OrderItemID] INT IDENTITY(1,1) PRIMARY KEY,
  [OrderID] INT NOT NULL,
  [product_id] INT NOT NULL,
  [Quantity] INT NOT NULL,
  [Price] DECIMAL(10,2) NOT NULL,

  CONSTRAINT [fk_OrderItem_Order] FOREIGN KEY ([OrderID])
  REFERENCES [Order]([OrderID]),

  CONSTRAINT [fk_OrderItem_Product] FOREIGN KEY ([product_id])
  REFERENCES [Product]([product_id])
);

CREATE TABLE [Rating] (
  [RatingID] INT IDENTITY(1,1) PRIMARY KEY,
  [user_id] INT NOT NULL,
  [product_id] INT NOT NULL,
  [vote] DECIMAL(10,2) NOT NULL,
  [comment] NVARCHAR(200),

  CONSTRAINT [fk_Rating_User] FOREIGN KEY ([user_id])
  REFERENCES [User]([id]),

  CONSTRAINT [fk_Rating_Product] FOREIGN KEY ([product_id])
  REFERENCES [Product]([product_id])
);

CREATE TABLE [Cart] (
   [cart_id] INT NOT NULL IDENTITY(1,1) PRIMARY KEY,
   [product_id] INT NOT NULL,
   [quantity] INT NOT NULL,
   [user_id] INT NOT NULL,

  CONSTRAINT [fk_Cart_Product] FOREIGN KEY ([product_id])
  REFERENCES [Product]([product_id]),

  CONSTRAINT [fk_Cart_User] FOREIGN KEY ([user_id])
  REFERENCES [User]([id])
);

